def compute_nbhadrons_in_jets(j1_p4, j2_p4, b_hadrons):
    v_j1 = j1_p4.Vect()
    v_j2 = j2_p4.Vect()
    n1 = 0
    n2 = 0
    
    for b in b_hadrons:
        v_b = b.Vect()        
        if v_b.Dot(v_j1)/(v_b.Mag()*v_j1.Mag()) > v_b.Dot(v_j2)/(v_b.Mag()*v_j2.Mag()):
            n1 += 1
        else:
            n2 += 1 
                       
    return n1, n2


def compute_analytical_rho(rho_b_clean, nd_clean, nd_same, ns_clean, ns_same, n0_clean, n0_same):
    C_clean = rho_b_clean + 1
    alpha = ns_same/ns_clean

    
    beta = nd_same/nd_clean
    gamma = n0_same/n0_clean
    #alpha = 0.6*gamma
    epsilon_1 = alpha/gamma
    epsilon_2 = beta/gamma
   

    print(f"gamma = {gamma:.4f}, beta = {beta:.4f}, alpha = {alpha:.4f}")
    print(f"eps1 = {epsilon_1:.4f}, eps2 = {epsilon_2:.4f}")
    
    correction = (1+gamma) * (1+beta) / (1+alpha)**2
    C = C_clean * correction
    rho_b = C - 1
    print(f"C_c = {C_clean:.4f}, correction = {correction:.4f}, C = {C:.4f}")
    print(f"rho_b_clean = {rho_b_clean:.4f}, rho_b = {rho_b:.4f}")
   

